import React from 'react'
import axios from 'axios';
import FormCreateUser from './FormCreateUser';

const baseUrl = "http://localhost:3000"
class Content extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      listUser:[]
    }
  }

  componentDidMount(){
     this.loadUser()
     const script = document.createElement("script");

        script.src = 'js/content.js';
        script.async = true;
        
        document.body.appendChild(script);
  }

  loadUser(){
    const url = baseUrl+"/user/list"
    axios.get(url)
    .then(res=>{
      if (res.data.success) {
        const data = res.data.data
        this.setState({listUser:data})
      }
      else {
        alert("Error web service")
      }
    })
    .catch(error=>{
      alert("Error server "+error)
    })
  } 

    render() {
        return (
            <div>
                <div className="content-wrapper">
                    {/* Main content */}
                    <section className="content">
                        <div className="row">
                        <div className="col-xs-12">
                            
                            {/* /.box */}
                            <div className="box">
                            <div className="box-header">

                            <section class="content-header">
                                <h3 className="box-title">Daftar User</h3>
                                <ol class="breadcrumb">
                                    <button type="button" className="btn btn-warning" data-toggle="modal" data-target="#modal-default">
                                    Create User
                                    </button>
                                </ol>
                                </section>
                                
                            </div>

                            <br></br>
                            <div className="modal fade" id="modal-default">
                                <div className="modal-dialog">
                                <div className="modal-content">
                                <div className="modal-header">
                                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">×</span></button>
                                    <h4 className="modal-title">Buat User Baru</h4>
                                </div>

                                <FormCreateUser/>

                                <div className="modal-footer">
                                    <button type="button" className="btn btn-default" data-dismiss="modal">Batal</button>
                                    <button type="submit" class="btn btn-primary" onClick={()=>this.sendSave()}>Simpan</button>
                                </div>
                                </div>
                                {/* /.modal-content */}
                            </div>
                            {/* /.modal-dialog */}
                            </div>

                            {/* /.box-header */}
                            <div className="box-body">

                                <table id="example1" className="table table-bordered table-striped">
                                <thead>
                                <tr>
                                    <th scope="col">Nama</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">No. Handphone</th>
                                    <th scope="col">Role</th>
                                </tr>
                                </thead>
                                <tbody>
                                
                                {this.loadFillData()}
                                </tbody>
                            </table>
                            </div>
                            {/* /.box-body */}
                            </div>
                            {/* /.box */}
                        </div>
                        {/* /.col */}
                        </div>
                        {/* /.row */}
                    </section>
                    {/* /.content */}
                </div>
            </div>
        );
    }

    loadFillData(){

      return this.state.listUser.map((data)=>{
        return(
          <tr>
            <td>{data.name}</td>
            <td>{data.email}</td>
            <td>{data.phone}</td>
            <td>{data.role.role}</td>
            
          </tr>
        )
      })
    }

    // Event to send and save data to the database on the Node.js server
    sendSave(){

      if (this.state.selectRole==0) {
        alert("Seleccione el tipo de Role")
      }
      else if (this.state.campPhone=="") {
         alert("Digite el campo de telefono")
      }
      else if (this.state.campName=="") {
         alert("Digite el campo de Nombre")
      }
      else if (this.state.campEmail=="") {
         alert("Digite el campo de email")
      }
      else if (this.state.campPassword=="") {
         alert("Digite el campo de Direccion")
      }
      else {
      
        console.log(this.state)
      //url backend
      const baseUrl = "http://localhost:3000/user/create"
      
      //post data parameters
      const datapost = {
          name : this.state.campName,
          email : this.state.campEmail,
          phone : this.state.campPhone,
          password : this.state.campPassword,
          role  : this.state.selectRole
        }
   
        axios.post(baseUrl,datapost)
        .then(response=>{
          if (response.data.success===true) {
            alert(response.data.message)
          }
          else {
            alert(response.data.message)
          }
        }).catch(error=>{
          alert("Error 34 "+error)
        })
   
      }
   
    }

}
export default Content;
