import React, { Component } from 'react'
import axios from 'axios';

const baseUrl = "http://localhost:3000"
class Content extends React.Component {

    constructor(props){
        super(props);
        this.state = {
          listUser:[]
        }
      }

    componentDidMount (){
        this.loadUser()

        const script = document.createElement("script");

        script.src = 'js/content.js';
        script.async = true;
        
        document.body.appendChild(script);

        
    }

    loadUser(){
        const url = baseUrl+"/user/list"
        axios.get(url)
        .then(res=>{
          if (res.data.success) {
            const data = res.data.data
            this.setState({listUser:data})
          }
          else {
            alert("Error web service")
          }
        })
        .catch(error=>{
          alert("Error server "+error)
        })
      }
    

    render() {
        return (
            <div>
                <div className="content-wrapper">
                    {/* Main content */}
                    <section className="content">
                        <div className="row">
                        <div className="col-xs-12">
                            
                            {/* /.box */}
                            <div className="box">
                            <div className="box-header">

                            <section class="content-header">
                                <h3 className="box-title">Daftar User</h3>
                                <ol class="breadcrumb">
                                    <button type="button" className="btn btn-warning" data-toggle="modal" data-target="#modal-default">
                                    Create User
                                    </button>
                                </ol>
                                </section>
                                
                            </div>

                            <br></br>
                            <div className="modal fade" id="modal-default">
                                <div className="modal-dialog">
                                <div className="modal-content">
                                <div className="modal-header">
                                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">×</span></button>
                                    <h4 className="modal-title">Buat User Baru</h4>
                                </div>
                                


                                <div className="box-body">
                                    <div className="form-group">
                                        <label htmlFor="exampleInputName1">Nama</label>
                                        <input type="name" className="form-control" id="exampleInputName1" />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputHandphone1">No. Handphone</label>
                                        <input type="handphone" className="form-control" id="exampleInputHandphone1"/>
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Email</label>
                                        <input type="email" className="form-control" id="exampleInputEmail1"/>
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputPassword1">Password</label>
                                        <input type="password" className="form-control" id="exampleInputPassword1"/>
                                    </div>
                                    <div class="form-group">
                                        <label>User Role</label>
                                            <select class="form-control" placeholder="Pilih Role">
                                                <option>Role 1</option>
                                                <option>Role 2</option>
                                                <option>Role 3</option>
                                             </select>
                                        </div>
                                    </div>
                                    


                                <div className="modal-footer">
                                    <button type="button" className="btn btn-default" data-dismiss="modal">Batal</button>
                                    <button type="button" className="btn btn-primary">Simpan</button>
                                </div>
                                </div>
                                {/* /.modal-content */}
                            </div>
                            {/* /.modal-dialog */}
                            </div>

                            {/* /.box-header */}
                            <div className="box-body">
                                {/* <table id="example1" className="table table-bordered table-striped">
                                <thead>
                                    <tr>
                                    <th>Nama</th>
                                    <th>Email</th>
                                    <th>No. Handphone</th>
                                    <th>Role</th>
                                    </tr>
                                </thead>
                                <tbody>
                                <tr>
                                    <td>John Smitth</td>
                                    <td>jhonsmith@mail.com</td>
                                    <td>82382193891</td>
                                    <td>Role 2</td>
                                </tr>
                                    {this.loadFillData()}
                                </tbody>
                                </table> */}

                                <table id="example1" className="table table-bordered table-striped">
                                <thead>
                                <tr>
                                    <th scope="col">Nama</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">No. Handphone</th>
                                    <th scope="col">Role</th>
                                </tr>
                                </thead>
                                <tbody>
                                
                                {this.loadFillData()}
                                </tbody>
                            </table>
                            </div>
                            {/* /.box-body */}
                            </div>
                            {/* /.box */}
                        </div>
                        {/* /.col */}
                        </div>
                        {/* /.row */}
                    </section>
                    {/* /.content */}
                </div>
            </div>
        );
    }

    loadFillData(){
        return this.state.listUser.map((data)=>{
            return(
              <tr>
                <td>{data.name}</td>
                <td>{data.email}</td>
                <td>{data.phone}</td>
                <td>{data.role.role}</td>
              </tr>
            )
          })
    }
}
export default Content;
