import React, { Component } from 'react';
import axios from 'axios';

import { FormWithConstraints, FieldFeedbacks, Async, FieldFeedback } from 'react-form-with-constraints';



class form extends Component{
constructor(props){
    super(props);

    this.state = {
      fields: {},
      errors: {}
    }
  }

  handleValidation(){
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    //Name
    if(!fields["name"]){
      formIsValid = false;
      errors["name"] = "Cannot be empty";
    }

    if(typeof fields["name"] !== "undefined"){
      if(!fields["name"].match(/^[a-zA-Z]+$/)){
        formIsValid = false;
        errors["name"] = "Only letters";
      }      	
    }

    //Email
    if(!fields["email"]){
      formIsValid = false;
      errors["email"] = "Cannot be empty";
    }

    if(typeof fields["email"] !== "undefined"){
      let lastAtPos = fields["email"].lastIndexOf('@');
      let lastDotPos = fields["email"].lastIndexOf('.');

      if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') == -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
        formIsValid = false;
        errors["email"] = "Email is not valid";
      }
    }





    this.setState({errors: errors});
    return formIsValid;
  }

  contactSubmit(e){
    e.preventDefault();
    if(this.handleValidation()){
      alert("Form submitted");
    }else{
      alert("Form has errors.")
    }

  }

  handleChange(field, e){    		
    let fields = this.state.fields;
    fields[field] = e.target.value;        
    this.setState({fields});
  }

  render(){
    return (
      <div>
        <div className="box-body">      	
        <form name="contactform" className="contactform" onSubmit= {this.contactSubmit.bind(this)}>

            <fieldset>
                <div className="form-group">
                <label htmlFor="exampleInputName1">Nama</label>
                  <input ref="name" type="text" class="form-control" size="30" placeholder="Name" onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]} Required/>
                  <span class="help-block">{this.state.errors["name"]}</span>
                </div>
                <div className="form-group">
                  <label htmlFor="exampleInputHandphone1">No. Handphone</label>
                  <input refs="phone" type="number" class="form-control" size="30" placeholder="Phone" onChange={this.handleChange.bind(this, "phone")} value={this.state.fields["phone"]}/>
                </div> 

                <div class="input-group">
                  <div class="input-group-addon">
                    <label>+62</label>
                  </div>
                  <input type="text" class="form-control" data-inputmask='"mask": "(999) 999-9999"' data-mask/>
                </div>

                <div className="form-group">
                  <label htmlFor="exampleInputEmail1">Email</label>
                  <input refs="email" type="email" class="form-control" size="30" placeholder="Email" onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]}/>
                  <span class="help-block">{this.state.errors["email"]}</span>
                </div>
                <div className="form-group">
                  <label htmlFor="exampleInputPassword1">Password</label>
                  <input refs="password" type="password" class="form-control" size="30" placeholder="Password" onChange={this.handleChange.bind(this, "password")} value={this.state.fields["password"]}/>
                  <span class="help-block">{this.state.errors["password"]}</span>
                </div>
                <div class="form-group">
                        <label for="inputState">User Role</label>
                        <select id="inputState" class="form-control" onChange={(value)=> this.setState({selectRole:value.target.value})}>
                            <option selected>Choose...</option>
                            <option value="1">Role 1</option>
                            <option value="2">Role 2</option>
                            <option value="3">Role 3</option>
                        </select>
                    </div>
            </fieldset>


          <div className="col-md-12">
            <div className="modal-footer">
                        <button type="button" className="btn btn-default" data-dismiss="modal">Batal</button>
                        <button type="submit" id="submit" class="btn btn-primary" >Simpan</button>
                    </div>
          </div>

        </form>
</div>  
      </div>
    )
  }
}

export default form;
